INSERT INTO `study_js_extends` VALUES (1, 3, 'replace()事件', '&lt;p&gt;replace() 方法用于在字符串中用一些字符替换另一些字符，或替换一个与正则表达式匹配的子串。&amp;nbsp;&lt;/p&gt;&lt;p&gt;语法： replace(/&lt;span style=&quot;font-weight: bold;&quot;&gt;obj&lt;/span&gt;/,&lt;span style=&quot;font-weight: bold;&quot;&gt;replacement&lt;/span&gt;)&lt;/p&gt;&lt;p&gt;简单应用：&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; var str=&quot;Visit Microsoft!&quot;&lt;br&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;str.replace(/Microsoft/,&quot;W3School&quot;);&amp;nbsp; &amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//简单替换&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; str.replace(/Microsoft/i,&quot;W3School&quot;);&amp;nbsp; &amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//对大小写不敏感&lt;br&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; str.replace(/Microsoft/g,&quot;W3School&quot;);&amp;nbsp; &amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//全局搜索&lt;/p&gt;&lt;p&gt;复杂应用：&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &lt;span style=&quot;background-color: rgb(194, 79, 74);&quot;&gt;？&lt;/span&gt;&lt;br&gt;&lt;/p&gt;', 1, 1550138001, 1, 1, 0);

