INSERT INTO `study_php_functions` VALUES (54, 0, 'str_replace()', 'string或array：替换字符串中的一些字符（区分大...', '&lt;p&gt;定义：替换字符串或数组中的一些字符（区分大小写）。&lt;/p&gt;&lt;p&gt;语法：str_replace(&lt;span style=&quot;font-weight: bold;&quot;&gt;find&lt;/span&gt; , &lt;span style=&quot;font-weight: bold;&quot;&gt;replace&lt;/span&gt; , &lt;span style=&quot;font-weight: bold;&quot;&gt;obj &lt;/span&gt;, count)&lt;/p&gt;&lt;p&gt;参数：&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; find&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; --要查找的字符串&lt;br&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; replace&amp;nbsp; &amp;nbsp;--接替的字符串&lt;br&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; obj&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;--对象&lt;br&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; count&amp;nbsp; &amp;nbsp; &amp;nbsp;--替换次数&lt;br&gt;&lt;/p&gt;&lt;p&gt;返回：字符串或数组&lt;/p&gt;&lt;p&gt;验证：&lt;/p&gt;&lt;div&gt;&lt;span&gt;&amp;lt;?php&lt;/span&gt;&lt;/div&gt;&lt;div&gt;&lt;span&gt;$a &lt;/span&gt;&lt;span&gt;=&lt;/span&gt;&lt;span&gt; &lt;/span&gt;&lt;span&gt;'11112222'&lt;/span&gt;&lt;span&gt;;&lt;/span&gt;&lt;/div&gt;&lt;div&gt;&lt;span&gt;echo&lt;/span&gt;&lt;span&gt; &lt;/span&gt;&lt;span&gt;str_replace&lt;/span&gt;&lt;span&gt;(&lt;/span&gt;&lt;span&gt;'2'&lt;/span&gt;&lt;span&gt;,&lt;/span&gt;&lt;span&gt;'1'&lt;/span&gt;&lt;span&gt;,$a);&lt;/span&gt;&lt;/div&gt;&lt;div&gt;&lt;span&gt;echo&lt;/span&gt;&lt;span&gt; &lt;/span&gt;&lt;span&gt;'&amp;lt;br/&amp;gt;'&lt;/span&gt;&lt;span&gt;;&lt;/span&gt;&lt;/div&gt;&lt;div&gt;&lt;span&gt;echo&lt;/span&gt;&lt;span&gt; &lt;/span&gt;&lt;span&gt;str_replace&lt;/span&gt;&lt;span&gt;(&lt;/span&gt;&lt;span&gt;array&lt;/span&gt;&lt;span&gt;(&lt;/span&gt;&lt;span&gt;'m'&lt;/span&gt;&lt;span&gt;,&lt;/span&gt;&lt;span&gt;'i'&lt;/span&gt;&lt;span&gt;),&lt;/span&gt;&lt;span&gt;'n'&lt;/span&gt;&lt;span&gt;,&lt;/span&gt;&lt;span&gt;&quot;my name is jim!&quot;&lt;/span&gt;&lt;span&gt;);&lt;/span&gt;&lt;/div&gt;&lt;div&gt;&lt;span&gt;echo&lt;/span&gt;&lt;span&gt; &lt;/span&gt;&lt;span&gt;'&amp;lt;br/&amp;gt;'&lt;/span&gt;&lt;span&gt;;&lt;/span&gt;&lt;/div&gt;&lt;div&gt;&lt;span&gt;echo&lt;/span&gt;&lt;span&gt; &lt;/span&gt;&lt;span&gt;@&lt;/span&gt;&lt;span&gt;str_replace&lt;/span&gt;&lt;span&gt;(&lt;/span&gt;&lt;span&gt;'m'&lt;/span&gt;&lt;span&gt;,&lt;/span&gt;&lt;span&gt;array&lt;/span&gt;&lt;span&gt;(&lt;/span&gt;&lt;span&gt;'n'&lt;/span&gt;&lt;span&gt;,&lt;/span&gt;&lt;span&gt;'z'&lt;/span&gt;&lt;span&gt;),&lt;/span&gt;&lt;span&gt;&quot;my name is jim!n&quot;&lt;/span&gt;&lt;span&gt;);&lt;/span&gt;&lt;/div&gt;&lt;div&gt;&lt;span&gt;echo&lt;/span&gt;&lt;span&gt; &lt;/span&gt;&lt;span&gt;'&amp;lt;br/&amp;gt;'&lt;/span&gt;&lt;span&gt;;&lt;/span&gt;&lt;/div&gt;&lt;div&gt;&lt;span&gt;echo&lt;/span&gt;&lt;span&gt; &lt;/span&gt;&lt;span&gt;@&lt;/span&gt;&lt;span&gt;str_replace&lt;/span&gt;&lt;span&gt;(&lt;/span&gt;&lt;span&gt;array&lt;/span&gt;&lt;span&gt;(&lt;/span&gt;&lt;span&gt;'m'&lt;/span&gt;&lt;span&gt;),&lt;/span&gt;&lt;span&gt;array&lt;/span&gt;&lt;span&gt;(&lt;/span&gt;&lt;span&gt;'n'&lt;/span&gt;&lt;span&gt;,&lt;/span&gt;&lt;span&gt;'z'&lt;/span&gt;&lt;span&gt;),&lt;/span&gt;&lt;span&gt;&quot;my name is jim!n&quot;&lt;/span&gt;&lt;span&gt;);&lt;/span&gt;&lt;/div&gt;&lt;div&gt;&lt;span&gt;echo&lt;/span&gt;&lt;span&gt; &lt;/span&gt;&lt;span&gt;'&amp;lt;br/&amp;gt;'&lt;/span&gt;&lt;span&gt;;&lt;/span&gt;&lt;/div&gt;&lt;div&gt;&lt;span&gt;$arr &lt;/span&gt;&lt;span&gt;=&lt;/span&gt;&lt;span&gt; &lt;/span&gt;&lt;span&gt;array&lt;/span&gt;&lt;span&gt;(&lt;/span&gt;&lt;span&gt;&quot;blue&quot;&lt;/span&gt;&lt;span&gt;,&lt;/span&gt;&lt;span&gt;&quot;red&quot;&lt;/span&gt;&lt;span&gt;,&lt;/span&gt;&lt;span&gt;&quot;green&quot;&lt;/span&gt;&lt;span&gt;,&lt;/span&gt;&lt;span&gt;&quot;yellow&quot;&lt;/span&gt;&lt;span&gt;);&lt;/span&gt;&lt;/div&gt;&lt;div&gt;&lt;span&gt;