INSERT INTO `study_php_uses` VALUES (2, 0, '实现中文字串截取无乱码的方法', '&lt;divcourier new',=&quot;&quot; monospace;font-weight:=&quot;&quot; normal;font-size:=&quot;&quot; 14px;line-height:=&quot;&quot; 19px;white-space:=&quot;&quot; pre;&quot;=&quot;&quot;&gt;&lt;divcourier new',=&quot;&quot; monospace;font-weight:=&quot;&quot; normal;font-size:=&quot;&quot; 14px;line-height:=&quot;&quot; 19px;white-space:=&quot;&quot; pre;&quot;=&quot;&quot;&gt;&lt;div&gt;/**&lt;/div&gt;&lt;div&gt; * 实现中文字串截取无乱码的方法&lt;/div&gt;&lt;div&gt; * @param   string  $string 要操作的字符串&lt;/div&gt;&lt;div&gt; * @param   number  $start  开始位置&lt;/div&gt;&lt;div&gt; * @param   integer $length 截取的长度&lt;/div&gt;&lt;div&gt; * @return  string  返回中文字符串&lt;/div&gt;&lt;div&gt; */&lt;/div&gt;&lt;div&gt;function getSubstr($string, $start, $length){&lt;/div&gt;&lt;div&gt;    &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if (mb_strlen($string, 'utf-8') &amp;gt; $length) {&lt;/div&gt;&lt;div&gt;        &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$str = mb_substr($string, $start, $length, 'utf-8');&lt;/div&gt;&lt;div&gt;        &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return $str . '...';&lt;/div&gt;&lt;div&gt;    &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;} else {&lt;/div&gt;&lt;div&gt;        &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return $string;&lt;/div&gt;&lt;div&gt;    &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&lt;/div&gt;&lt;div&gt;}&lt;/div&gt;&lt;/divcourier&gt;&lt;/divcourier&gt;', 0, 1551358244, 0, 0, 0);
INSERT INTO `study_php_uses` VALUES (3, 0, '计算字节数', '&lt;div&gt;&lt;span&gt;/**&lt;/span&gt;&lt;/div&gt;&lt;div&gt;&lt;span&gt; * 格式化字节大小&lt;/span&gt;&lt;/div&gt;&lt;div&gt;&lt;span&gt; * &lt;/span&gt;&lt;span&gt;@param&lt;/span&gt;&lt;span&gt;  &lt;/span&gt;&lt;span&gt;number&lt;/span&gt;&lt;span&gt; $size      字节数&lt;/span&gt;&lt;/div&gt;&lt;div&gt;&lt;span&gt; * &lt;/span&gt;&lt;span&gt;@param&lt;/span&gt;&lt;span&gt;  &lt;/span&gt;&lt;span&gt;string&lt;/span&gt;&lt;span&gt; $delimiter 数字和单位分隔符&lt;/span&gt;&lt;/div&gt;&lt;div&gt;&lt;span&gt; * &lt;/span&gt;&lt;span&gt;@return&lt;/span&gt;&lt;span&gt; &lt;/span&gt;&lt;span&gt;string&lt;/span&gt;&lt;span&gt;            格式化后的带单位的大小&lt;/span&gt;&lt;/div&gt;&lt;div&gt;&lt;span&gt; */&lt;/span&gt;&lt;/div&gt;&lt;div&gt;&lt;span&gt;function&lt;/span&gt;&lt;span&gt; &lt;/span&gt;&lt;span&gt;format_bytes&lt;/span&gt;&lt;span&gt;($size, $delimiter &lt;/span&gt;&lt;span&gt;=&lt;/span&gt;&lt;span&gt; &lt;/span&gt;&lt;span&gt;''&lt;/span&gt;&lt;span&gt;) {&lt;/span&gt;&lt;/div&gt;&lt;div&gt;&lt;span&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$units &lt;/span&gt;&lt;span&gt;=&lt;/span&gt;&lt;span&gt; &lt;/span&gt;&lt;span&gt;array&lt;/span&gt;&lt;span&gt;(&lt;/span&gt;&lt;span&gt;'B'&lt;/span&gt;&lt;span&gt;, &lt;/span&gt;&lt;span&gt;'KB'&lt;/span&gt;&lt;span&gt;, &lt;/span&gt;&lt;span&gt;'MB'&lt;/span&gt;&lt;span&gt;, &lt;/span&gt;&lt;span&gt;'GB'&lt;/span&gt;&lt;span&gt;, &lt;/span&gt;&lt;span&gt;'TB'&lt;/span&gt;&lt;span&gt;, &lt;/span&gt;&lt;span&gt;'PB'&lt;/span&gt;&lt;span&gt;);&lt;/span&gt;&lt;/div&gt;&lt;div&gt;&lt;span&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;span&gt;for&lt;/span&gt;&lt;span&gt; ($i &lt;/span&gt;&lt;span&gt;=&lt;/span&gt;&lt;span&gt; &lt;/span&gt;&lt;span&gt;0&lt;/span&gt;&lt;span&gt;; $size &lt;/span&gt;&lt;span&gt;&amp;gt;=&lt;/span&gt;&lt;span&gt; &lt;/span&gt;&lt;span&gt;1024&lt;/span&gt;&lt;span&gt; &lt;/span&gt;&lt;span&gt;&amp;amp;&amp;amp;&lt;/span&gt;&lt;span&gt; $i &lt;/span&gt;&lt;span&gt;&amp;lt;&lt;/span&gt;&lt;span&gt; &lt;/span&gt;&lt;span&gt;5&lt;/span&gt;&lt;span&gt;; $i&lt;/span&gt;&lt;span&gt;++&lt;/span&gt;&lt;span&gt;) $size &lt;/span&gt;&lt;span&gt;/=&lt;/span&gt;&lt;span&gt; &lt;/span&gt;&lt;span&gt;1024&lt;/span&gt;&lt;span&gt;;&lt;/span&gt;&lt;/div&gt;&lt;div&gt;&lt;span&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;span&gt;return&lt;/span&gt;&lt;span&gt; &lt;/span&gt;&lt;span&gt;round&lt;/span&gt;&lt;span&gt;($size, &lt;/span&gt;&lt;span&gt;2&lt;/span&gt;&lt;span&gt;) &lt;/span&gt;&lt;span&gt;.&lt;/span&gt;&lt;span&gt; $delimiter &lt;/span&gt;&lt;span&gt;.&lt;/span&gt;&lt;span&gt; $units[$i];&lt;/span&gt;&lt;/div&gt;&lt;p&gt;&lt;divcourier new',=&quot;&quot; monospace;font-weight:=&quot;&quot; normal;font-size:=&quot;&quot; 14px;line-height:=&quot;&quot; 19px;white-space:=&quot;&quot; pre;&quot;=&quot;&quot;&gt;&lt;/divcourier&gt;&lt;/p&gt;&lt;div&gt;&lt;span&gt;}&lt;/span&gt;&lt;/div&gt;', 0, 1548671385, 0, 0, 0);
INSERT INTO `study_php_uses` VALUES (4, 0, '签名，32位随机字符串', '&lt;divcourier new',=&quot;&quot; monospace;font-weight:=&quot;&quot; normal;font-size:=&quot;&quot; 14px;line-height:=&quot;&quot; 19px;white-space:=&quot;&quot; pre;&quot;=&quot;&quot;&gt;&lt;div&gt;/**&lt;/div&gt;&lt;div&gt; * 数字和字母的随机组合：&lt;/div&gt;&lt;div&gt; * @param   integer $len    长度&lt;/div&gt;&lt;div&gt; * @param   string  $chars  资源(default null)&lt;/div&gt;&lt;div&gt; * @return  string  $str    组合字符串           &lt;/div&gt;&lt;div&gt; */&lt;/div&gt;&lt;div&gt;function getRandomString($len, $chars=null){&lt;/div&gt;&lt;div&gt;    &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if (is_null($chars)){&lt;/div&gt;&lt;div&gt;        &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$chars = &quot;abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789&quot;;&lt;/div&gt;&lt;div&gt;    &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}  &lt;/div&gt;&lt;div&gt;    &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;for ($i = 0, $str = '', $lc = strlen($chars)-1; $i &amp;lt; $len; $i++){&lt;/div&gt;&lt;div&gt;        &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$str .= $chars[mt_rand(0, $lc)];  &lt;/div&gt;&lt;div&gt;&amp;nbsp; &amp;nbsp; }&lt;/div&gt;&lt;div&gt;    &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return $str;&lt;/div&gt;&lt;div&gt;}&lt;/div&gt;&lt;/divcourier&gt;', 0, 1551358170, 0, 0, 0);
INSERT INTO `study_php_uses` VALUES (5, 0, '定义目录', '&lt;p&gt;&lt;divcourier new',=&quot;&quot; monospace;font-weight:=&quot;&quot; normal;font-size:=&quot;&quot; 14px;line-height:=&quot;&quot; 19px;white-space:=&quot;&quot; pre;&quot;=&quot;&quot;&gt;&lt;/divcourier&gt;&lt;/p&gt;&lt;div&gt;&lt;span&gt;define&lt;/span&gt;&lt;span&gt;(&lt;/span&gt;&lt;span&gt;'DIR_SMARTY'&lt;/span&gt;&lt;span&gt;,&lt;/span&gt;&lt;span&gt;str_ireplace&lt;/span&gt;&lt;span&gt;(&lt;/span&gt;&lt;span&gt;'&lt;/span&gt;&lt;span&gt;\\&lt;/span&gt;&lt;span&gt;'&lt;/span&gt;&lt;span&gt;,&lt;/span&gt;&lt;span&gt;'/'&lt;/span&gt;&lt;span&gt;,&lt;/span&gt;&lt;span&gt;dirname&lt;/span&gt;&lt;span&gt;(&lt;/span&gt;&lt;span&gt;__FILE__&lt;/span&gt;&lt;span&gt;)&lt;/span&gt;&lt;span&gt;.&lt;/span&gt;&lt;span&gt;'/'&lt;/span&gt;&lt;span&gt;));&lt;/span&gt;&lt;/div&gt;', 0, 1551358078, 0, 0, 0);
INSERT INTO `study_php_uses` VALUES (6, 0, '创建目录', '&lt;div&gt;&lt;span&gt;/**&lt;/span&gt;&lt;/div&gt;&lt;div&gt;&lt;span&gt; * 判断文件夹是否存在，不存在则创建：&lt;/span&gt;&lt;/div&gt;&lt;div&gt;&lt;span&gt; * &lt;/span&gt;&lt;span&gt;@param&lt;/span&gt;&lt;span&gt;   &lt;/span&gt;&lt;span&gt;string&lt;/span&gt;&lt;span&gt;  $dir    目录名(路径) &lt;/span&gt;&lt;/div&gt;&lt;div&gt;&lt;span&gt; * &lt;/span&gt;&lt;span&gt;@param&lt;/span&gt;&lt;span&gt;   &lt;/span&gt;&lt;span&gt;integer&lt;/span&gt;&lt;span&gt; $mode   &lt;/span&gt;&lt;/div&gt;&lt;div&gt;&lt;span&gt; * &lt;/span&gt;&lt;span&gt;@return&lt;/span&gt;&lt;span&gt;    &lt;/span&gt;&lt;/div&gt;&lt;div&gt;&lt;span&gt; */&lt;/span&gt;&lt;/div&gt;&lt;div&gt;&lt;span&gt;function&lt;/span&gt;&lt;span&gt; &lt;/span&gt;&lt;span&gt;mkdirs&lt;/span&gt;&lt;span&gt;($dir, $mode &lt;/span&gt;&lt;span&gt;=&lt;/span&gt;&lt;span&gt; &lt;/span&gt;&lt;span&gt;0777&lt;/span&gt;&lt;span&gt;){&lt;/span&gt;&lt;/div&gt;&lt;div&gt;&lt;span&gt;&lt;/span&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;span&gt;if&lt;/span&gt;&lt;span&gt; (&lt;/span&gt;&lt;span&gt;is_dir&lt;/span&gt;&lt;span&gt;($dir) &lt;/span&gt;&lt;span&gt;||&lt;/span&gt;&lt;span&gt; &lt;/span&gt;&lt;span&gt;@&lt;/span&gt;&lt;span&gt;mkdir&lt;/span&gt;&lt;span&gt;($dir, $mode)) &lt;/span&gt;&lt;span&gt;return&lt;/span&gt;&lt;span&gt; &lt;/span&gt;&lt;span&gt;TRUE&lt;/span&gt;&lt;span&gt;;&lt;/span&gt;&lt;/div&gt;&lt;div&gt;&lt;span&gt;&lt;/span&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;span&gt;if&lt;/span&gt;&lt;span&gt; (&lt;/span&gt;&lt;span&gt;!&lt;/span&gt;&lt;span&gt;mkdirs&lt;/span&gt;&lt;span&gt;(&lt;/span&gt;&lt;span&gt;dirname&lt;/span&gt;&lt;span&gt;($dir), $mode)) &lt;/span&gt;&lt;span&gt;return&lt;/span&gt;&lt;span&gt; &lt;/span&gt;&lt;span&gt;FALSE&lt;/span&gt;&lt;span&gt;;&lt;/span&gt;&lt;/div&gt;&lt;div&gt;&lt;span&gt;&lt;/span&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;span&gt;return&lt;/span&gt;&lt;span&gt; &lt;/span&gt;&lt;span&gt;@&lt;/span&gt;&lt;span&gt;mkdir&lt;/span&gt;&lt;span&gt;($dir, $mode);&lt;/span&gt;&lt;/div&gt;&lt;p&gt;&lt;divcourier new',=&quot;&quot; monospace;font-weight:=&quot;&quot; normal;font-size:=&quot;&quot; 14px;line-height:=&quot;&quot; 19px;white-space:=&quot;&quot; pre;&quot;=&quot;&quot;&gt;&lt;/divcourier&gt;&lt;/p&gt;&lt;div&gt;&lt;span&gt;}&lt;/span&gt;&lt;/div&gt;', 0, 1551358121, 0, 0, 0);
INSERT INTO `study_php_uses` VALUES (7, 0, '清理缓存', '&lt;div&gt;&lt;span&gt;/**&lt;/span&gt;&lt;/div&gt;&lt;div&gt;&lt;span&gt; * 清理缓存:清除缓存的所有文件&lt;/span&gt;&lt;/div&gt;&lt;div&gt;&lt;span&gt; * &lt;/span&gt;&lt;span&gt;@param&lt;/span&gt;&lt;span&gt;   &lt;/span&gt;&lt;span&gt;string&lt;/span&gt;&lt;span&gt;  $dir    目录&lt;/span&gt;&lt;/div&gt;&lt;div&gt;&lt;span&gt; * &lt;/span&gt;&lt;span&gt;@return&lt;/span&gt;&lt;span&gt;  &lt;/span&gt;&lt;span&gt;boolean&lt;/span&gt;&lt;span&gt; true    &lt;/span&gt;&lt;/div&gt;&lt;div&gt;&lt;span&gt; */&lt;/span&gt;&lt;/div&gt;&lt;div&gt;&lt;span&gt;function&lt;/span&gt;&lt;span&gt; &lt;/span&gt;&lt;span&gt;deldir&lt;/span&gt;&lt;span&gt;($dir) {     &lt;/span&gt;&lt;/div&gt;&lt;div&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;span&gt;$dh &lt;/span&gt;&lt;span&gt;=&lt;/span&gt;&lt;span&gt; &lt;/span&gt;&lt;span&gt;@&lt;/span&gt;&lt;span&gt;opendir&lt;/span&gt;&lt;span&gt;($dir);&lt;/span&gt;&lt;/div&gt;&lt;div&gt;&lt;span&gt;&lt;/span&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;span&gt;while&lt;/span&gt;&lt;span&gt; ($file &lt;/span&gt;&lt;span&gt;=&lt;/span&gt;&lt;span&gt; &lt;/span&gt;&lt;span&gt;@&lt;/span&gt;&lt;span&gt;readdir&lt;/span&gt;&lt;span&gt;($dh)) {&lt;/span&gt;&lt;/div&gt;&lt;div&gt;&lt;span&gt;&lt;/span&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span&gt;if&lt;/span&gt;&lt;span&gt;($file &lt;/span&gt;&lt;span&gt;!=&lt;/span&gt;&lt;span&gt;&quot;.&quot;&lt;/span&gt;&lt;span&gt; &lt;/span&gt;&lt;span&gt;&amp;amp;&amp;amp;&lt;/span&gt;&lt;span&gt; $file &lt;/span&gt;&lt;span&gt;!=&lt;/span&gt;&lt;span&gt;&quot;..&quot;&lt;/span&gt;&lt;span&gt;) {&lt;/span&gt;&lt;/div&gt;&lt;div&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span&gt;$fullpath &lt;/span&gt;&lt;span&gt;=&lt;/span&gt;&lt;span&gt; $dir&lt;/span&gt;&lt;span&gt;.&lt;/span&gt;&lt;span&gt;&quot;/&quot;&lt;/span&gt;&lt;span&gt;.&lt;/span&gt;&lt;span&gt;$file;&lt;/span&gt;&lt;/div&gt;&lt;div&gt;&lt;span&gt;&lt;/span&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span&gt;if&lt;/span&gt;&lt;span&gt;(&lt;/span&gt;&lt;span&gt;!&lt;/span&gt;&lt;span&gt;is_dir&lt;/span&gt;&lt;span&gt;($fullpath)) {&lt;/span&gt;&lt;/div&gt;&lt;div&gt;&lt;span&gt;&lt;/span&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span&gt;unlink&lt;/span&gt;&lt;span&gt;($fullpath);&lt;/span&gt;&lt;/div&gt;&lt;div&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span&gt;} &lt;/span&gt;&lt;span&gt;else&lt;/span&gt;&lt;span&gt; {&lt;/span&gt;&lt;/div&gt;&lt;div&gt;&lt;span&gt;&lt;/span&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span&gt;deldir&lt;/span&gt;&lt;span&gt;($fullpath);&lt;/span&gt;&lt;/div&gt;&lt;div&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span&gt;}&lt;/span&gt;&lt;/div&gt;&lt;div&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span&gt;}&lt;/span&gt;&lt;/div&gt;&lt;div&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;span&gt;}&lt;/span&gt;&lt;/div&gt;&lt;div&gt;&lt;span&gt;&lt;/span&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;span&gt;@&lt;/span&gt;&lt;span&gt;closedir&lt;/span&gt;&lt;span&gt;($dh);&lt;/span&gt;&lt;/div&gt;&lt;div&gt;&lt;span&gt;&lt;/span&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;span&gt;return&lt;/span&gt;&lt;span&gt; &lt;/span&gt;&lt;span&gt;true&lt;/span&gt;&lt;span&gt;;&lt;/span&gt;&lt;/div&gt;&lt;p&gt;&lt;divcourier new',=&quot;&quot; monospace;font-weight:=&quot;&quot; normal;font-size:=&quot;&quot; 14px;line-height:=&quot;&quot; 19px;white-space:=&quot;&quot; pre;&quot;=&quot;&quot;&gt;&lt;/divcourier&gt;&lt;/p&gt;&lt;div&gt;&lt;span&gt;}&lt;/span&gt;&lt;/div&gt;', 0, 1551358291, 0, 0, 0);

