INSERT INTO `study_php_functions` VALUES (50, 2, 'defined()', 'boolean：检查某常量是否存在', '&lt;p&gt;defined() 函数检查某常量是否存在。&lt;/p&gt;&lt;p&gt;若常量存在，则返回 true，否则返回 false。&lt;/p&gt;&lt;p&gt;语法：defined(&lt;span style=&quot;font-weight: bold;&quot;&gt;str&lt;/span&gt;)&lt;/p&gt;&lt;p&gt;参数：&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; str&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;--常量名称&lt;br&gt;&lt;/p&gt;&lt;p&gt;验证：&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; const SSS = 'sgy';&lt;/p&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;define('dYG','345');&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;echo defined('SSS');&amp;nbsp; &amp;nbsp; &amp;nbsp;// 1&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; echo defined('dYG');&amp;nbsp; &amp;nbsp; // 1&lt;br&gt;&lt;/p&gt;', 0, 1550128209, 0, 0, 0);
INSERT INTO `study_php_functions` VALUES (51, 0, 'ini_get()', 'string：获取某一个配置项的值（列出所有配置）', '&lt;p&gt;语法：ini_get(&lt;span style=&quot;font-weight: bold;&quot;&gt;$str&lt;/span&gt;)&lt;/p&gt;&lt;p&gt;配置信息：（$str可能的值）&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; default_charset&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; ：当前编码字符集（默认UTF-8）&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; max_execution_time&amp;nbsp; &amp;nbsp; ：最大执行时间&lt;br&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; upload_max_filesize&amp;nbsp; &amp;nbsp; &amp;nbsp;：最大上传限制&lt;br&gt;&lt;/p&gt;&lt;p&gt;&lt;br&gt;&lt;/p&gt;&lt;p&gt;&lt;br&gt;&lt;/p&gt;', 0, 1550401582, 1, 0, 0);
