INSERT INTO `study_save_specials` VALUES (1, 1, 0, 'Mysql 查询当前数据上一条和下一条的记录', '&lt;p&gt;参考链接：https://www.cnblogs.com/apolloren/p/9062946.html&lt;/p&gt;&lt;p&gt;获取当前文件上一条与下一条记录的原理是上一条的sql语句，从news表里按从大到小的顺序选择一条比当前ID小的新闻,下一条的sql语句，从news表里按从小到大的顺序选择一条比当前ID大的新闻。&lt;/p&gt;&lt;p&gt;如果ID是主键或者有索引，可以直接查找：&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;方法1：&lt;/p&gt;&lt;p&gt;1.select&amp;nbsp;*&amp;nbsp;from&amp;nbsp;table_a&amp;nbsp;where&amp;nbsp;id&amp;nbsp;=&amp;nbsp;(select&amp;nbsp;id&amp;nbsp;from&amp;nbsp;table_a&amp;nbsp;where&amp;nbsp;id&amp;nbsp;&amp;lt;&amp;nbsp;{$id}&amp;nbsp;order&amp;nbsp;by&amp;nbsp;id&amp;nbsp;desc&amp;nbsp;limit&amp;nbsp;1);&amp;nbsp;&lt;/p&gt;&lt;p&gt;2.select&amp;nbsp;*&amp;nbsp;from&amp;nbsp;table_a&amp;nbsp;where&amp;nbsp;id&amp;nbsp;=&amp;nbsp;(select&amp;nbsp;id&amp;nbsp;from&amp;nbsp;table_a&amp;nbsp;where&amp;nbsp;id&amp;nbsp;&amp;gt;&amp;nbsp;{$id}&amp;nbsp;order&amp;nbsp;by&amp;nbsp;id&amp;nbsp;asc&amp;nbsp;limit&amp;nbsp;1);&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;br&gt;方法2：&lt;/p&gt;&lt;p&gt;1.select&amp;nbsp;*&amp;nbsp;from&amp;nbsp;table_a&amp;nbsp;where&amp;nbsp;id&amp;nbsp;=&amp;nbsp;(select&amp;nbsp;max(id)&amp;nbsp;from&amp;nbsp;table_a&amp;nbsp;where&amp;nbsp;id&amp;nbsp;&amp;lt;&amp;nbsp;{$id});&amp;nbsp;&lt;/p&gt;&lt;p&gt;2.select&amp;nbsp;*&amp;nbsp;from&amp;nbsp;table_a&amp;nbsp;where&amp;nbsp;id&amp;nbsp;=&amp;nbsp;(select&amp;nbsp;min(id)&amp;nbsp;from&amp;nbsp;table_a&amp;nbsp;where&amp;nbsp;id&amp;nbsp;&amp;gt;&amp;nbsp;{$id});&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;div&gt;&lt;ol start=&quot;1&quot;&gt;&lt;br&gt;&lt;/ol&gt;&lt;/div&gt;', 0, 1552552294, 1, 0, 0);

