INSERT INTO `study_software_errors` VALUES (1, 3, 1, 'Git Bash下cat命令读取文件显示乱码', '&lt;p&gt;在&lt;em&gt;git bash&lt;/em&gt;下使用&lt;code&gt;cat &amp;lt;filename&amp;gt;&lt;/code&gt;命令时，如果文件中存在中文字符，则控制台上的会出现乱码，在网上找了很多解决方法，都没能解决，最后只能找到一个临时解决方案：&amp;nbsp;&lt;br&gt;&lt;code&gt;$ cat test.txt | iconv -f GBK -t UTF-8&lt;/code&gt;&amp;nbsp;&amp;nbsp;&lt;br&gt;&lt;/p&gt;', 0, 1551772199, 0, 0, 0);

